const article=document.querySelector(".shoe-care"),backToBlogs=document.querySelector(".footer__item-ref");export const fullBlog=async()=>{article.innerHTML="";const t=new URLSearchParams(location.search).get("id"),e=await fetch(`https://gorest.co.in/public-api/posts?id=${t}`),o=await e.json(),a=await fetch(`https://gorest.co.in/public-api/users/${o.data[0].user_id}`),n=await a.json(),c=document.createElement("h1");c.style.marginBottom="50px",c.textContent=o.data[0].title;const s=document.createElement("p");s.textContent=o.data[0].body;const i=document.querySelector(".footer__item-author");i.style.marginBottom="25px",i.textContent=n.data.name;const l=document.createElement("p");l.classList.add("blog__item-subtitle"),l.style.marginBottom="25px",l.textContent="22 октября 2021, 12:45";const r=document.createElement("p");r.classList.add("blog__item-media");const m=document.createElement("span");m.classList.add("blog__item-views"),m.textContent="1.2K";const d=document.createElement("span");d.classList.add("blog__item-comments"),d.textContent="0",backToBlogs.href="blog.html",r.append(m,d),i.after(l,r),article.append(c,s)};