import{loadPosts}from"./data.js";const blogWrapper=document.querySelector(".blog-wrapper");export const createPosts=async()=>{const t=new URLSearchParams(location.search).get("page"),e=await loadPosts(t),a=document.createElement("ul");a.classList.add("blog__list"),blogWrapper.append(a),e.data.forEach(((t,s)=>{const d=document.createElement("li");d.classList.add("blog__item");const o=document.createElement("div");o.classList.add("blog__item-img-wrapper");const c=document.createElement("a");c.classList.add("blog__item-img"),c.href=`article.html?id=${e.data[s].id}`,c.style.backgroundImage='url("./img/blog/boots.png")';const n=document.createElement("div");n.classList.add("blog__text-wrapper");const l=document.createElement("a");l.classList.add("blog__item-title"),l.textContent=t.title,l.href=`article.html?id=${e.data[s].id}`;const i=document.createElement("a");i.classList.add("blog__item-subtitle"),i.href=`article.html?id=${e.data[s].id}`,i.textContent="22 октября 2021, 12:45";const m=document.createElement("a");m.classList.add("blog__item-media"),m.href=`article.html?id=${e.data[s].id}`;const r=document.createElement("span");r.classList.add("blog__item-views"),r.textContent="1.2K";const p=document.createElement("span");p.classList.add("blog__item-comments"),p.textContent="0",o.append(c),d.append(o,n),n.append(l,i,m),m.append(r,p),a.append(d)}))};