import{loadPosts}from"./data.js";const blogWrapper=document.querySelector(".blog-wrapper");export const pagination=async()=>{const e=new URLSearchParams(location.search).get("page"),t=await loadPosts(e),a=document.createElement("div");a.classList.add("pages");const n=document.createElement("a");n.classList.add("pages__btn-previous");const o=document.createElement("ul");o.classList.add("pages__list");const p=document.createElement("a");p.classList.add("pages__item"),1===t.meta.pagination.page?(p.textContent="",n.style.display="none"):p.textContent=t.meta.pagination.page-1;const s=p.cloneNode();s.textContent=t.meta.pagination.page;const i=s.cloneNode(),g=document.createElement("a");g.classList.add("pages__btn-next"),t.meta.pagination.page===t.meta.pagination.pages?(i.textContent="",g.style.display="none"):i.textContent=t.meta.pagination.page+1,g.addEventListener("click",(()=>{t.meta.pagination.page+=1,g.href=`blog.html?page=${t.meta.pagination.page}`})),n.addEventListener("click",(()=>{t.meta.pagination.page-=1,1===t.meta.pagination.page?n.href="blog.html":n.href=`blog.html?page=${t.meta.pagination.page}`})),a.append(n,o,g),o.append(p,s,i),o.childNodes.forEach((e=>{e.addEventListener("click",(()=>{e.textContent===1..toString()?e.href="blog.html":e.href=`blog.html?page=${e.textContent}`}))})),blogWrapper.after(a)};